codes = [
    "1122",
    "1111",
    "1234",
    "91212129",
    ""
]

codes2 = [
    "1212",
    "1221",
    "123425",
    "123123",
    "12131415",
    ""
]

def captcha1(number):
    next_number = number[1:] + number[0]
    result = 0
    for (x, y) in zip(number, next_number):
        if x == y:
            result += int(x)
    return result

def captcha2(number):
    step = int(len(number) / 2)
    next_number = number[step:] + number[:step]
    result = 0
    for (x, y) in zip(number, next_number):
        if x == y:
            result += int(x)
    return result

for code in codes:
    print(captcha1(code))

for code in codes2:
    print(captcha2(code))
